// Declarations para imports de activos estáticos y CSS
declare module '*.css'
declare module '*.scss'
declare module '*.sass'

// Imágenes / iconos
declare module '*.svg'
declare module '*.png'
declare module '*.jpg'
declare module '*.jpeg'
declare module '*.gif'
declare module '*.ico'

// Si usas módulos CSS (css modules) también puedes añadir:
declare module '*.module.css'
declare module '*.module.scss'
